mapper.select:
SELECT
-- 级联表的字段 ,boke.boke_name as bokeName ,boke.boke_types as bokeTypes ,boke.boke_photo as bokePhoto ,boke.boke_video as bokeVideo ,boke.boke_file as bokeFile ,boke.boke_music as bokeMusic ,boke.zan_number as zanNumber ,boke.cai_number as caiNumber ,boke.yonghu_id as bokeYonghuId ,boke.boke_neirong_types as bokeNeirongTypes ,boke.boke_content as bokeContent ,boke.boke_yesno_types as bokeYesnoTypes ,boke.boke_yesno_text as bokeYesnoText ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM boke_liuyan a left JOIN boke boke ON a.boke_id = boke.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.boke_id = #{params.bokeId} )
and ( a.yonghu_id = #{params.yonghuId} or boke.yonghu_id = #{params.yonghuId} )
and a.boke_liuyan_text like CONCAT('%',#{params.bokeLiuyanText},'%')
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.boke_id IS NOT NULL
and boke.boke_name like CONCAT('%',#{params.bokeName},'%')
and boke.boke_types = #{params.bokeTypes}
and boke.boke_video like CONCAT('%',#{params.bokeVideo},'%')
and boke.boke_music like CONCAT('%',#{params.bokeMusic},'%')
and boke.zan_number >= #{params.zanNumberStart}
and boke.zan_number <= #{params.zanNumberEnd}
and boke.zan_number = #{params.zanNumber}
and boke.cai_number >= #{params.caiNumberStart}
and boke.cai_number <= #{params.caiNumberEnd}
and boke.cai_number = #{params.caiNumber}
and boke.boke_neirong_types = #{params.bokeNeirongTypes}
and boke.boke_content like CONCAT('%',#{params.bokeContent},'%')
and boke.boke_yesno_types = #{params.bokeYesnoTypes}
and boke.boke_yesno_text like CONCAT('%',#{params.bokeYesnoText},'%')
and UNIX_TIMESTAMP(boke.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(boke.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
